optimized-checkout.scss:
// =============================================================================
// Stencil Checkout - Customize the Optimized Single-Page Checkout experience
// =============================================================================

// =============================================================================
//
// IMPORTANT DISCLAIMER
// Please do not nest elements within class selectors, and do not use class selectors other than those given below.
// Future support is guaranteed only if class selectors' structure and naming are left unchanged.
//
// =============================================================================

//
// Header Section
// Background, Logo Position
// -----------------------------------------------------------------------------

*:focus {
    outline-color: stencilColor("optimizedCheckout-colorFocus");
}

.optimizedCheckout-header {
    background-color: stencilColor("optimizedCheckout-header-backgroundColor");
    // scss-lint:disable StringQuotes
    background-image: url(stencilImage('optimizedCheckout-backgroundImage', 'optimizedCheckout-backgroundImage-size'));
    // scss-lint:enable StringQuotes
    background-size: cover;
    border-color: stencilColor("optimizedCheckout-header-borderColor");
    color: stencilColor("optimizedCheckout-header-textColor");

    //
    // TODO:
    // Configure media query to be 'desktop' breakpoint size
    // -----------------------------------------------------------------------------
    @media (min-width: 801px) {
        text-align: stencilString("optimizedCheckout-logo-position");
    }
}

//
// Primary and Secondary Headings
// Text Color & Typography
// .optimizedCheckout-headingPrimary styles top-level headings.
// .optimizedCheckout-headingSecondary styles certain lower-level elements, such as descriptions of cart items.
// -----------------------------------------------------------------------------

.optimizedCheckout-headingPrimary {
    color: stencilColor("optimizedCheckout-headingPrimary-color");
    font-family: stencilFontFamily("optimizedCheckout-headingPrimary-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-headingPrimary-font");
}

.optimizedCheckout-headingSecondary {
    color: stencilColor("optimizedCheckout-headingSecondary-color");
    font-family: stencilFontFamily("optimizedCheckout-headingSecondary-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-headingSecondary-font");
}

//
// Body
// Background & Shipping Method Overlay
// -----------------------------------------------------------------------------

body {
    background-color: stencilColor("optimizedCheckout-body-backgroundColor");
}

.optimizedCheckout-overlay {
    background-color: stencilColor("optimizedCheckout-body-backgroundColor");
    border-color: stencilColor("optimizedCheckout-step-borderColor");
    color: stencilColor("optimizedCheckout-contentPrimary-color");
}

//
// Primary & Secondary Content
// Text Color & Typography
// .optimizedCheckout-contentPrimary styles body text in the Order Summary/Order Confirmation; drop-down-list items; and text that summarizes completed steps.
// .optimizedCheckout-contentSecondary styles check boxes' text labels, and lower-level text in the Order Summary.
// -----------------------------------------------------------------------------

.optimizedCheckout-contentPrimary {
    color: stencilColor("optimizedCheckout-contentPrimary-color");
    font-family: stencilFontFamily("optimizedCheckout-contentPrimary-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-contentPrimary-font");
}

.optimizedCheckout-contentSecondary {
    color: stencilColor("optimizedCheckout-contentSecondary-color");
    font-family: stencilFontFamily("optimizedCheckout-contentSecondary-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-contentSecondary-font");
}

//
// Primary Button
// Background & Border & Text Color & Typography
// Styles the CONTINUE button for each step, & the final confirmation button.
// -----------------------------------------------------------------------------

.optimizedCheckout-buttonPrimary {
    background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColor");
    border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColor");
    color: stencilColor("optimizedCheckout-buttonPrimary-color");
    font-family: stencilFontFamily("optimizedCheckout-buttonPrimary-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-buttonPrimary-font");

    &:focus,
    &:hover {
        background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColorHover");
        border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColorHover");
        color: stencilColor("optimizedCheckout-buttonPrimary-colorHover");
    }

    &:focus {
        border-color: stencilColor("optimizedCheckout-colorFocus");
        box-shadow: 0 0 3px stencilColor("optimizedCheckout-colorFocus");
    }

    &:active {
        background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColorActive");
        border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColorActive");
        color: stencilColor("optimizedCheckout-buttonPrimary-colorActive");
    }

    &[disabled],
    &[disabled]:hover,
    &[disabled]:focus,
    &[disabled].is-active,
    &[disabled].is-loading,
    &[disabled].active {
        background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColorDisabled");
        border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColorDisabled");
        color: stencilColor("optimizedCheckout-buttonPrimary-colorDisabled");
    }
}

//
// Secondary Button
// Background & Border & Text Color & Typography
// Styles the EDIT button for each step.
// -----------------------------------------------------------------------------

.optimizedCheckout-buttonSecondary {
    background-color: stencilColor("optimizedCheckout-buttonSecondary-backgroundColor");
    border-color: stencilColor("optimizedCheckout-buttonSecondary-borderColor");
    color: stencilColor("optimizedCheckout-buttonSecondary-color");
    font-family: stencilFontFamily("optimizedCheckout-buttonSecondary-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-buttonSecondary-font");

    &:focus,
    &:hover {
        background-color: stencilColor("optimizedCheckout-buttonSecondary-backgroundColorHover");
        border-color: stencilColor("optimizedCheckout-buttonSecondary-borderColorHover");
        color: stencilColor("optimizedCheckout-buttonSecondary-colorHover");
    }

    &:focus {
        border-color: stencilColor("optimizedCheckout-colorFocus");
        box-shadow: 0 0 3px stencilColor("optimizedCheckout-colorFocus");
    }

    &:active {
        background-color: stencilColor("optimizedCheckout-buttonSecondary-backgroundColorActive");
        border-color: stencilColor("optimizedCheckout-buttonSecondary-borderColorActive");
        color: stencilColor("optimizedCheckout-buttonSecondary-colorActive");
    }

    &[disabled],
    &[disabled]:hover,
    &[disabled]:focus,
    &[disabled].is-active,
    &[disabled].is-loading,
    &[disabled].active {
        background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColorDisabled");
        border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColorDisabled");
        color: stencilColor("optimizedCheckout-buttonPrimary-colorDisabled");
    }
}

//
// Links
// Text Color & Typography
// -----------------------------------------------------------------------------

a {
    color: stencilColor("optimizedCheckout-link-color");
    font-family: stencilFontFamily("optimizedCheckout-link-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-link-font");

    &:focus,
    &:hover {
        color: stencilColor("optimizedCheckout-link-hoverColor");
    }
}

//
// Order Summary (Mobile Drawer & Modal)
// Background Color
// -----------------------------------------------------------------------------

.optimizedCheckout-orderSummary {
    background-color: stencilColor("optimizedCheckout-orderSummary-backgroundColor");
    border-color: stencilColor("optimizedCheckout-orderSummary-borderColor");
    box-shadow: 0 4px 8px transparentize(stencilColor("optimizedCheckout-orderSummary-borderColor"), 0.5);
}

.optimizedCheckout-orderSummary-cartSection {
    border-color: stencilColor("optimizedCheckout-orderSummary-borderColor");
}

.optimizedCheckout-checkoutStep {
    border-color: stencilColor("optimizedCheckout-step-borderColor");
}

//
// Checkout Steps
// Background & Text Color
// Styles the large step-number indicators on the page's left side.
// -----------------------------------------------------------------------------

.optimizedCheckout-step {
    background-color: stencilColor("optimizedCheckout-step-backgroundColor");

    &::before {
        color: stencilColor("optimizedCheckout-step-textColor");
    }

    svg {
        fill: stencilColor("optimizedCheckout-step-textColor");
    }
}

//
// Form Labels
// Text Color
// Styles text labels on form fields.
// -----------------------------------------------------------------------------

.optimizedCheckout-form-label {
    color: stencilColor("optimizedCheckout-form-textColor");
}

.form-field--error {
    .optimizedCheckout-form-label,
    .form-inlineMessage {
        color: stencilColor("optimizedCheckout-formField-errorColor");
    }
}
//
// Form Input
// Background, Border, Shadow and Text Color
// Styles form fields.
// -----------------------------------------------------------------------------

$optimizedCheckout-input-boxShadow: inset 0 1px 1px stencilColor("optimizedCheckout-formField-shadowColor");
$optimizedCheckout-input--focus-boxShadow: 0 0 3px transparentize(stencilColor("optimizedCheckout-colorFocus"), 0.4);
$optimizedCheckout-input--error-boxShadow: 0 0 3px transparentize(stencilColor("optimizedCheckout-formField-errorColor"), 0.4);

.optimizedCheckout-form-input,
.optimizedCheckout-form-select {
    background-color: stencilColor("optimizedCheckout-formField-backgroundColor");
    border-color: stencilColor("optimizedCheckout-formField-borderColor");
    box-shadow: $optimizedCheckout-input-boxShadow;
    color: stencilColor("optimizedCheckout-formField-textColor");

    &::placeholder {
        color: stencilColor("optimizedCheckout-formField-placeholderColor");
    }

    .form-field--error & {
        background-color: stencilColor("optimizedCheckout-formField-backgroundColor");
        border-color: stencilColor("optimizedCheckout-formField-errorColor");
        box-shadow: $optimizedCheckout-input--error-boxShadow, $optimizedCheckout-input-boxShadow;
    }
}

.optimizedCheckout-form-input:focus,
.optimizedCheckout-form-select:focus,
.optimizedCheckout-form-select:hover {
    background-color: stencilColor("optimizedCheckout-formField-backgroundColor");
    border-color: stencilColor("optimizedCheckout-colorFocus");
    box-shadow: $optimizedCheckout-input--focus-boxShadow, $optimizedCheckout-input-boxShadow;
}

.optimizedCheckout-form-label::before {
    .optimizedCheckout-form-radio + &,
    .optimizedCheckout-form-checkbox + & {
        background-color: stencilColor("optimizedCheckout-formField-backgroundColor");
        border-color: stencilColor("optimizedCheckout-formField-borderColor");
        box-shadow: $optimizedCheckout-input-boxShadow;
        opacity: 1;
    }

    .optimizedCheckout-form-radio:checked + &,
    .optimizedCheckout-form-checkbox:checked + & {
        background-color: stencilColor("optimizedCheckout-formField-inputControlColor");
        border-color: stencilColor("optimizedCheckout-formField-inputControlColor");
        box-shadow: none;
    }

    .optimizedCheckout-form-radio:focus + &,
    .optimizedCheckout-form-checkbox:focus + & {
        border-color: stencilColor("optimizedCheckout-colorFocus");
        box-shadow: $optimizedCheckout-input--focus-boxShadow, $optimizedCheckout-input-boxShadow;
    }

    .optimizedCheckout-form-radio:focus:checked + &,
    .optimizedCheckout-form-checkbox:focus:checked + & {
        box-shadow: $optimizedCheckout-input--focus-boxShadow;
    }
}

//
// Discount Banners
// Background & Text & Icon Color
// Styles discount banners.
// -----------------------------------------------------------------------------

.optimizedCheckout-discountBanner {
    background-color: stencilColor("optimizedCheckout-discountBanner-backgroundColor");
    color: stencilColor("optimizedCheckout-discountBanner-textColor");
    font-family: stencilFontFamily("optimizedCheckout-link-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-link-font");

    svg {
        fill: stencilColor("optimizedCheckout-discountBanner-iconColor");
    }
}

//
// Form Checklist (Shipping / Payment methods)
// Background & Border & Text Color
// Styles form checklists
// -----------------------------------------------------------------------------

.optimizedCheckout-form-checklist {
    background-color: stencilColor("optimizedCheckout-formChecklist-backgroundColor");
    border-color: stencilColor("optimizedCheckout-formChecklist-borderColor");
    color: stencilColor("optimizedCheckout-formChecklist-color");
}

.optimizedCheckout-form-checklist-item {
    background-color: stencilColor("optimizedCheckout-formChecklist-backgroundColor");
    border-color: stencilColor("optimizedCheckout-formChecklist-borderColor");
}

.optimizedCheckout-form-checklist-item:hover,
.optimizedCheckout-form-checklist-item--selected {
    background-color: stencilColor("optimizedCheckout-formChecklist-backgroundColorSelected");
    border-color: stencilColor("optimizedCheckout-formChecklist-borderColor");
}

.optimizedCheckout-form-checklist-checkbox {
    ~ .form-label::after,
    ~ .form-label::before {
        background-color: stencilColor("optimizedCheckout-formField-backgroundColor");
        border-color: stencilColor("optimizedCheckout-formField-borderColor");
    }

    &:focus ~ .form-label {
        box-shadow: inset 0 0 0 1px stencilColor("optimizedCheckout-colorFocus");
    }

    &:checked ~ .form-label::before {
        border-color: stencilColor("optimizedCheckout-formField-inputControlColor");
    }

    &:checked ~ .form-label::after {
        background: stencilColor("optimizedCheckout-formField-inputControlColor");
    }
}

//
// Loading Toaster
// Background & Text Color
// Styles the Loading Toaster
// -----------------------------------------------------------------------------

.optimizedCheckout-loadingToaster {
    background-color: stencilColor("optimizedCheckout-loadingToaster-backgroundColor");
    color: stencilColor("optimizedCheckout-loadingToaster-textColor");
}

.modal.modal--afterOpen.optimizedCheckout-contentPrimary{
    visibility: visible !important;
    display: block !important;
    max-width: 100%;
    overflow-y: scroll;
}

@import "roots/mods";

roots/mods.scss:
// ROOTS STYLES

// FONTS
@font-face{
	font-family: 'Gotham';
	font-style: normal;
	font-weight: 400;
	src: url('../fonts/Gotham-Book.woff') format('woff');
}
@font-face{
	font-family: 'Gotham';
	font-style: normal;
	font-weight: 500;
	src: url('../fonts/Gotham-Medium.woff') format('woff');
}
@font-face{
	font-family: 'Gotham';
	font-style: normal;
	font-weight: 600;
	src: url('../fonts/Gotham-Bold.woff') format('woff');
}

// VARIABLES
// general
$logo-width: get-width(stencilString('logo_size'));
$content-maxWidth: 1230px; // includes padding. make 'none' for full width
$productGrid-imageHeight: #{stencilNumber("card-image-ratio", "") * 285}px;
$productGrid-imageHeight--tablet: #{stencilNumber("card-image-ratio", "") * 374}px;
$productGrid-imageHeight--mobile: #{stencilNumber("card-image-ratio", "") * 224}px;
$subcategoryGrid-imageHeight: #{stencilNumber("subcategory-image-ratio", "") * 285}px;
$logo_fontSize: stencilNumber("logo_fontSize");
$logo_fontSize_mobile: stencilNumber("logo_fontSize_mobile");

// colours
$button--primary-backgroundColor: stencilColor("button--primary-backgroundColor");
$button--secondary-backgroundColor: stencilColor("button--default-backgroundColor");
$button--secondary-text: stencilColor("button--default-color");
$button--secondary-text-hover: stencilColor("button--default-colorHover");

$navUser-subMenu-background: stencilColor("navUser-dropdown-backgroundColor");
$nav-backgroundColor: stencilColor("navPages-backgroundColor");
$nav-linkColor: stencilColor("navPages-color");
$nav-linkHover: stencilColor("navPages-color-hover");
$nav-subMenu-background: stencilColor("navPages-subMenu-backgroundColor");
$nav-subMenu-backgroundHover: stencilColor("navPages-subMenu-backgroundColorHover");
$navCart-icon-mobile: stencilColor("navUser-cart-icon-mobile");
$nav-toggle-mobile: stencilColor("nav-toggle-mobile");
$cart-count-bg-mobile: stencilColor("navUser-indicator-backgroundColor-mobile");
$navPage-borderColor: stencilColor("navPages-subMenu-separatorColor");

$search-bg: stencilColor("search-background");
$quickSearch-backgroundColor: stencilColor("dropdown--quickSearch-backgroundColor");
$search-boxColor: stencilColor("search-box");
$search-textColor: stencilColor("search-text");

$general-linkColor: stencilColor("color-textLink");
$general-linkColorHover: stencilColor("color-textLink--hover");
$baseText-color: stencilColor("color-textBase");
$header-footer-separator-line-color: stencilColor("header-footer-separator-line-color");

$card-titleColor: stencilColor("card-title-color");
$card-image-border: stencilColor("card-image-border");

$alerts--backgroundColor: stencilColor("alerts--backgroundColor");
$alerts--textColor: stencilColor("alerts--textColor");
$alerts--linkColor: stencilColor("alerts--linkColor");

$footer-headingColor: stencilColor("footer-heading-fontColor");
$footer-textColor: stencilColor("footer-textColor");
$footer-linkColor: stencilColor("footer-linkColor");
$footer-linkColorHover: stencilColor("footer-linkColor-hover");

$carousel-textColor: stencilColor("carousel-description-color");
$carousel-textBackground: stencilColor("carousel-bgColor");
$carousel-textBackgroundOpacity: stencilNumber("carousel-textBgOpacity", "");
$carousel-ArrowBackground: stencilColor("carousel-arrow-bgColor");
$carousel-ArrowBackgroundOpacity: stencilNumber("carousel-arrow-bgOpacity", "");
$carousel-button-color: stencilColor("carousel-button-color");
$carousel-button-border-color: stencilColor("carousel-button-border-color");

$carousel-mobile-headingColor: stencilColor("carousel-mobile-title-color");
$carousel-mobile-textColor: stencilColor("carousel-mobile-description-color");
$carousel-mobile-textBackground: stencilColor("carousel-mobile-bgColor");
$carousel-mobile-button-textColor: stencilColor("carousel-mobile-button-color");
$carousel-mobile-button-border: stencilColor("carousel-mobile-button-border-color");

// ICONS
$social-media-icons--footer: stencilColor("social-media-icons-footer");
$social-media-icons--footerHover: stencilColor("social-media-icons-footer--hover");
$payment-icons-footer: stencilColor("payment-icons-footer");

// BADGES
$stockBadge-bgColor: stencilColor("stockBadge-bgColor");
$stockBadge-textColor: stencilColor("stockBadge-textColor");

$red: #F54A3A;

// GRID
$numProds--grid-desktop: 100/4;
$numProds--grid-desktop-leftCol: 100/3;
$numProds--grid-tablet: 100/2;
$numProds--grid-mobile: 100/2;

// PRODUCT PAGE
$pdp-image-width: get-width(stencilString('product_size'));

// FONTS
$headings-font: 'Gotham';
$body-font: 'Gotham';
$buttons-font: 'Gotham';
$navPages-font: 'Gotham';
$cardTitle-font: 'Gotham';

// MISC
$navPages-grow: stencilNumber("navPages-grow", "");
$subtleGrey: #ebebeb;

// GENERAL
body{
    transition: padding-top .3s linear;
    font-family: $body-font;
}

h1, h2, h3, h4, h5, h6{
    font-family: $body-font;
}

.main-heading{
    font-family: $headings-font;
    font-size: 32px;
    text-transform: none;
    line-height: normal;
}
.page-heading{
    color: stencilColor("color-textHeading");
    font-family: $headings-font;
    font-weight: 400;
}
.uppercase{text-transform: uppercase;}
.center{text-align: center;}
a{text-decoration: none;}

.breadcrumbs{
    display: block;
    margin-bottom: 10px;
}

.breadcrumbs,
.body:not(.home) .page-heading{text-align: left;}

.button{
    font-family: $buttons-font;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 15px;
    border-radius: 2px;
}

.button:not(.button--icon) i{
    max-height: 1rem;
    max-width: 1rem;
}

.swal2-modal .swal2-cancel,
.swal2-modal .swal2-cancel:active,
.swal2-modal .swal2-cancel:focus,
.swal2-modal .swal2-cancel:hover{
    background: $button--secondary-backgroundColor;
    color: $button--secondary-text;
}

.page{margin: 0;}

.uppercase{text-transform: uppercase;}

.button.dropdown-menu-button svg{
    fill: $button--secondary-text;
}

.socialLinks .icon svg{fill: #838a91;}
.socialLinks .icon:hover svg{fill: #a5a5a5;}

.navList-section:after,
.navList-section:before,
.navList:after,
.navList:before{display: none;}

// slick slider
.slick-prev,
.slick-next{
    top: 50%;
    transform: translateY(-50%);
    margin: 0;
    width: auto;
    height: auto;
}
.slick-prev,
.slick-next,
.slick-prev:hover,
.slick-next:hover,
.slick-prev:focus,
.slick-next:focus{
    background-color: transparent;
}

.slick-prev:before,
.slick-next:before{
    height: 20px;
    width: 15px;
    background-size: 100% auto;
}

.slick-slider:not(.heroCarousel) .slick-prev:before{
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' width='24' height='42' viewBox='0 0 24 42'%3E%3Cpath d='M22.4572074 1.00746147l-21 20.02482143 20.9479397 19.9751786' stroke='%23000000' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' fill='none' fill-rule='evenodd' /%3E%3C/svg%3E");
}
.slick-slider:not(.heroCarousel) .slick-next:before{
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' width='24' height='42' viewBox='0 0 24 42'%3E%3Cpath d='M1.45679 1.00746147l21 20.02482143L1.50885 41.0074615' stroke='%23000000' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' fill='none' fill-rule='evenodd' /%3E%3C/svg%3E");
}

.slick-disabled{opacity: .3;}

// alerts
.alertBox--info{
    background-color: $alerts--backgroundColor;
    color: $alerts--textColor; 
}

.alertBox--info a{color: $alerts--linkColor;}

.container,
.banner,
.previewCartAction,
.previewCartItem{
    max-width: $content-maxWidth;
    padding: 0 15px;
    width: 100%;
    min-width: 0;
}

.container{
    clear: both;
    float: none;
}

.grid-bg{
    background: #fff;
    overflow: hidden;
    padding-top: 40px;
}

.grid-bg + .container{
    margin-top: 3rem;
}

.navUser{
    max-width: $content-maxWidth;
    width: 100%;
    font-size: 1rem;
}

.navPages{
    max-width: $content-maxWidth;
    width: 100%;
}

.navUser,
.navPages,
.heroCarousel{
    margin: 0 auto;
}

// SOCIAL LINKS
.socialLinks,
footer .socialLinks{
    display: flex;
    flex-flow: row wrap;
}
.socialLinks .socialLinks-item{
    margin: 0 14px 14px 0;
    display: flex;
    flex-flow: row nowrap;
    justify-content: center;
    align-items: center;
}

// PRODUCT GRID
.brandGrid:after,
.brandGrid:before,
.productGrid:before,
.productGrid:after,
.productView:after,
.productView:before{display: none;}

.productGrid,
.brandGrid{
    display: flex;
    flex-flow: row wrap;
}
.productGrid .product,
.brandGrid .brand{
    flex: 0 1 #{$numProds--grid-desktop}%;
    margin-bottom: 20px;
    max-width: #{$numProds--grid-desktop}%;
    overflow: visible;
}

.productGrid .card,
.brandGrid .card,
.productCarousel-slide .card,
.card{
//     align-items: flex-start;
    display: flex;
    flex-flow: column nowrap;
    height: 100%;
    margin-bottom: 0;
}
.card .card-figure{
    display: flex;
    flex: 0 1 auto;
    flex-flow: column nowrap;
//     height: $productGrid-imageHeight;
    justify-content: center;
    background: transparent;
}

// .card-img-container:after{display: none;}

.card .card-figure a{position: relative;}

.card .card-image{
//     max-height: calc(#{$productGrid-imageHeight} - 2px);
//     position: static;
}

.card:hover .card-figcaption{
    height: auto;
    max-height: 300px;
    padding: 10px;
    z-index: 20;
}

.card-figcaption{
    border: 0;
    bottom: 0;
    height: auto;
    top: auto;
    opacity: 1;
    height: 0;
    overflow: hidden;
    max-height: 0;
    transition: max-height .5s linear;
    width: calc(100% + 2px);
    left: -1px;
}
.card-figcaption .button{font-size: 13px;}

.card-figcaption-body{
    color: $general-linkColor;
    transform: none;
    width: 100%;
    text-align: left;
}

.button.button--secondary{
    background: stencilColor("card--alternate-backgroundColor");
    border: 1px solid stencilColor("card-figcaption-button-background");
    color: stencilColor("card-figcaption-button-background");
}
.button.button--secondary svg{
    fill: stencilColor("card-figcaption-button-background");
}
.card-figcaption-body .button:not(.quickview){
