}
.heroCarousel.slick-slider{
    margin-bottom: 0;
    width: 1290px;
    max-width: 100%;
    min-width: auto;
}

.slick-slide{height: auto;}

.heroCarousel-title{
    font-family: $headings-font;
    font-weight: bold;
    text-transform: uppercase;
    font-size: 32px;
}
.heroCarousel-title + .heroCarousel-description{
    margin-top: 17px;
}
.heroCarousel-action,
.heroCarousel-action:hover,
.heroCarousel-action:focus{
    background: transparent;
    border: 1px solid $carousel-button-border-color;
    color: $carousel-button-color;
    text-transform: uppercase;
}

.heroCarousel .slick-next::before,
.heroCarousel  .slick-prev::before{
    background-size: 100% auto;
    height: 20px;
    width: 15px;
}

.heroCarousel .slick-prev,
.heroCarousel .slick-next{
    background-color: rgba($carousel-ArrowBackground, $carousel-ArrowBackgroundOpacity);
    height: auto;
    opacity: 0;
    padding: 10px;
    -webkit-transition: opacity .3s ease-in-out;
    transition: opacity .3s ease-in-out;
    width: auto;
}

.heroCarousel:hover .slick-prev,
.heroCarousel:hover .slick-next{
    opacity: 1;
}

.heroCarousel .slick-dots li{
    background: transparent;
    height: auto;
    width: auto;
    text-align: center;
}

.slick-dots li button,
.slick-dots li button::before{
    border-width: 2px;
    height: 10px;
    width: 10px;
}
.slick-dots li button::before{
    top: -2px;
    left: -2px;
}
.heroCarousel .slick-dots li button:before,
.heroCarousel .slick-dots li button:hover:before,
.heroCarousel .slick-dots li button:focus:before{
    opacity: 1;
}

