}

// FAST CART
.fastCart .whatNext{flex: 1 1 100%;}
.fastCart .productView{
    flex-wrap: wrap;
    align-items: flex-start;
}
.fastCart .productView-image{
    width: 150px;
}
.fastCart .productView-image{min-height: 0;}
.fastCart .productView-details{
    width: auto;
