    flex: 1 1 calc(100% - 150px);
}

.fastCart .productView .productView-price{font-size: 14px;}

.fastCart .previewCartCheckout{
    display: flex;
    flex-flow: row wrap;
    justify-content: space-between;
}

.fastCart .previewCartCheckout .button{
