    padding-left: 15px;
    padding-right: 15px;
    margin-top: 5px;
    margin-bottom: 5px;
}
.fastCart .previewCartCheckout > div{
    flex: 0 1 48%;
}

.fastCart .previewCartCheckout > div p{margin-bottom: 0;}

.fastCart .previewCart:after{display: none;}
.fastCart .suggestiveCart{clear: both;}


// ACCOUNT PAGES
.navBar--account{display: block;}

.wishlistGrid article{
    max-height: calc(100% - 40px);
}
.wishlist-item-remove{
    margin: 8px 0 0;
}
.wishlist-item-remove input{
    margin: 0;
    padding-left: 15px;
    padding-right: 15px;
    white-space: normal;
    width: 100%;
}

// SEARCH PAGE
.category-suggestion{
    width: 100%;
    float: none;
}

// ACCOUNT PAGES
.panel--address .form-actions{
    display: flex;
    flex-flow: row wrap;
}

// hotel finder
.hotelSearchWrap{
    display: flex;
    flex-flow: row wrap;
    align-items: center;
    position: relative;
    justify-content: space-between;
    margin-bottom: 10px;
}
.hotelSearch{
    padding: 4px 8px;
    width: 100%;
    height: 42px;
    max-width: calc(100% - 160px);
    min-width: 200px;
    margin-bottom: 10px;
}
.hotelSearchWrap button{
    padding: 12px 10px;
    flex: 0 0 150px;
    margin-bottom: 10px;
    font-size: 13px;
    height: 42px;
}
.hotelResults{
    position: absolute;
    background: #fff;
    max-height: 400px;
    overflow-y: auto;
    top: calc(100% - 10px);
}

.hotelResults:not(.noResults){
    border: 1px solid rgba(0,0,0,.2);
}

.hotelResults ul{list-style: none; margin: 0;}
.hotelResults li:first-child{margin-top: 10px;}
.hotelResults li:last-child{margin-bottom: 10px;}
