        display: inline;
    }
    .header-logo-image{
        display: block;
        max-height: 50px;
        position: relative;
        margin: auto;
    }
    .header-logo-image-container:after{padding: 0;}

    .navUser{
        padding: 0;
        top: 0;
        transform: none;
        width: auto;
    }
    .navUser .navUser-section,
    .navUser-item{
        float: none;
        margin: 0 15px;
        text-align: center;
    }

    .navUser-section{
        display: flex;
        flex-flow: row nowrap;
        align-items: center;
    }

    .navUser .navUser-item--mobile-search{
        display: block;
        padding-right: 15px;
        position: relative;
        transition: all .3s linear;
        cursor: pointer;
    }

    .navUser-item--cart{
        display: flex;
        flex-flow: row wrap;
        align-items: center;
    }
    .navUser-item--cart .navUser-action{
        font-size: 14px;
        padding: 0;
        width: 100%;
    }

    .navUser-item-cartLabel{display: none;}

    .navUser .countPill{
        background: $cart-count-bg-mobile;
        color: $navCart-icon-mobile;
    }

    .quickSearchWrap{
        position: absolute;
        bottom: -1px;
        left: 0;
        width: 100%;
    }

    .quickSearchWrap form,
    .quickSearchWrap .form-field{margin: 0;}

    .quickSearchWrap .form-field{
        position: relative;
    }

    .quickSearchWrap .form-field .form-input{
        background: #f9f9f9;
        border-width: 1px 0 1px 0;
        border-color: $subtleGrey;
        padding-left: 30px;
        padding-right: 30px;
        text-align: center;
    }
    .quickSearchWrap .form-button{padding-right: 0;}

    .quickSearchResults{
        margin: 0;
        position: absolute;
        top: 100%;
        z-index: 50;
    }

    .quickSearchResults .modal-close{display: block;}

    .quickSearchResults{margin-top: -1px;}

    // navigation
    body.has-activeNavPages .header-logo{opacity: 0;}
    .navPages .socialLinks{padding-left: 15px; padding-right: 15px;}
    .navPages .socialLinks svg{
        fill: $nav-linkColor;
    }
    .navPages .socialLinks a:hover svg{
        fill: $nav-linkHover;
    }

    .navPages-list--user{
        display: flex;
        align-items: center;
        flex-flow: row wrap;
        justify-content: space-between;
    }

    .navPages-list--user .navPages-item, 
    .navPages-list--user .navPages-action{
        display: inline-block;
        font-size: 14px;
    }

    .navPages-item--currency{
        border-left: 1px solid #D2D2D2;
        border-right: 1px solid #D2D2D2;
        border-top: 1px solid #fff;
    }
    .navPages-item--currency.link-expanded{
        border-top: 1px solid #D2D2D2;
    }
    .navPages-item--currency > a{
        padding: 5px 10px;
    }
    .navPages-item--currency .navPage-subMenu-action{
        padding: 3px 15px;
    }
    .navPages-item--currency .navPages-action-moreIcon{
        width: 15px;
        height: 10px;
        padding-left: 5px;
    }

    .navPages-item--currency .navPage-subMenu{
        background: #fff;
        position: absolute;
        right: 0;
        z-index: 1;
        margin-top: -1px;
    }
    .navPages-item--currency .navPage-subMenu ul{
        border: 1px solid $navPage-borderColor;
        padding: 5px 0;
    }

    .navPages-mainNav > li{
        border-bottom: 1px solid $navPage-borderColor;
    }

    .navPages-list + .navPages-mainNav {
        border-top: 1px solid $navPage-borderColor;
    }

    .navPages-item{float: none; font-size: 0;}
    .navPages-item > a{font-size: 15px;}
    .navPages-container.is-open{
        background: rgba(0,0,0,.5);
        padding-top: 0;
        z-index: 50;
    }

    li.link-expanded > div{
        display: block;
    }

    .navPage-subMenu > ul{margin-left: 0px;}

    .navPage-subMenu-item{
        display: flex;
        align-items: center;
    }
    .navPage-subMenu-item > a{
        display: inline-block;
        vertical-align: middle;
        flex: 1 1 auto;
        padding-left: 5px;
    }

    .navPages-item--content-first{border-top: 3px solid $navPage-borderColor;}

    .navPage-subMenu-item.is-open .navPages-action-closed{display: none;}
    .navPage-subMenu-item:not(.is-open) .navPages-action-open{display: none;}
    .navPage-subMenu-item a ~ .navPages-action-moreIcon{
        flex: 0 0 42px;
        padding: 5px 15px;
        width: 42px;
        height: 22px;
        cursor: pointer;
    }
    .navPage-childList-item{margin-left: 25px;}
    .navPage-childList-item:not(.is-open){display: none;}
    .mobile-closeNav{
        position: absolute;
        right: 20px;
        top: 20px;
        font-size: 0;
        cursor: pointer;
    }
    .mobile-closeNav .icon{
        width: 35px;
        height: 35px;
    }
    .mobile-closeNav .icon svg{
        fill: #fff;
    }

    .navPages{width: calc(100% - 75px); margin-left: 0;}

    .navPages-item{color: $nav-linkColor;}

    .navPage-subMenu-item + .navPages-action-more{margin-left: -5px;}

    .navPage-subMenu-item ul,
    .navPage-subMenu-item div{display: none;}

    .navPages-secondLevel{
        width: 7px;
        height: 10px;
    }

    .link-expanded > ul,
    .link-expanded > div{
        background: rgba(255,255,255,.2);
    }

    .navPages-action{
        padding: 10px 0px;
        position: relative;
    }

    .navPages-action.has-subMenu.is-open{
        border: 0;
        text-align: left;
    }

    .navPages-mainNav .navPages-action.has-subMenu .navPages-action-moreIcon,
    .navPages-mainNav .navPages-action.has-subMenu.is-open .navPages-action-moreIcon{
        border: 0;
        border-radius: 0;
        height: 100%;
        margin: 0;
        padding: 0 15px;
        width: 42px;
        height: 100%;
        position: absolute;
        right: 0;
        top: 0;
    }

    .navPages-item.link-expanded > a > .navPages-action-moreIcon{
        -ms-transform: rotate(180deg);
        -webkit-transform: rotate(180deg);
        transform: rotate(180deg);
    }

    .navPages-action.has-subMenu.is-open svg{
        width: 100%;
        height: 100%;
    }

    .navPages .navPage-subMenu-list,
    .navPage-childList{border: 0; margin: 0;}

    .navPage-subMenu-item-child .navPage-subMenu-item-child > a{
        font-weight: 400;
        text-transform: none;
        padding-left: 35px;
    }

    .navPage-subMenu-item-child .navPage-subMenu-item-child > a .navPages-secondLevel{display: none;}

    // carousel
    .heroCarousel-content{
        padding: 27px 20px 40px;
    }
    .heroCarousel-title{margin: 0;}
    .heroCarousel-title + .heroCarousel-description{margin-top: 10px;}
    .slick-dots{
        display: none !important;
    }

    .heroCarousel-content{background: $carousel-mobile-textBackground;}
    .heroCarousel-title{color: $carousel-mobile-headingColor;}
    .heroCarousel-description{color: $carousel-mobile-textColor;}
    .heroCarousel-action,
    .heroCarousel-action:hover,
    .heroCarousel-action:focus{
        color: $carousel-mobile-button-textColor;
        border-color: $carousel-mobile-button-border;
    }

    .heroCarousel + .banners{padding-top: 0;}

    .heroCarousel + .banners .featured-categories{padding-top: 0;}

    // home page
    .featured-categories{
        margin: 0;
        padding-left: 0;
        padding-right: 0;
    }
    .featured-categories > div{
        flex: 0 1 auto;
        margin: 0 auto 10px;
    }

    .blog:not(.full-post){
        flex: 0 1 100%;
        width: 100%;
        padding-left: 0;
        padding-right: 0;
    }

    .blog-posts{margin: 0;}

    // footer
    .footer-newsletter{padding-bottom: 20px;}

    .footer-newsletter .springmod-form .form-group:not(.ng-scope){margin-top: 10px;}

    .footer-newsletter .springmod-form .form-group:not(.ng-scope),{flex: 0 0 100%;}

    .footer-newsletter .springmod-form .form-group:not(.ng-scope) .button{width: 100%;}

    .footer section + section{padding-top: 0;}

    .footer-info-col,
    .footer-info-col.footer-info-col-multi > div:first-child:not(:last-child){
        border-bottom: 1px solid #dcdcdc;
        margin: 0;
        flex: 1 1 100%;
    }
    .footer-info-col-multi{
        padding-left: 0;
        padding-right: 0;
    }
    .footer-info-col-multi > div{
        padding-left: 15px;
        padding-right: 15px;
    }
    .footer-info-col.footer-newsletter-col{
        flex: 1 1 100%;
    }

    .footer-giftCerts{
        text-align: center;
        margin: 15px 0;
    }

    .footer-mobile-limited{
        max-width: calc(100% - 50px);
        margin: auto;
        text-align: center;
        padding-top: 45px;
        padding-bottom: 45px;
    }

    .footer-info-dropdown:not(.is-open) ul{
        display: none;
    }

    .footer-info-dropdown h5{
        cursor:pointer;
        margin: 0;
        display: flex;
        flex-flow: row wrap;
        align-items: center;
        justify-content: space-between;
        padding-top: 15px;
        padding-bottom: 15px;
    }

    .footer-info-list{
        padding-bottom: 15px;
    }
    .footer-info-list li{
        padding: 12px 0;
    }

    footer .socialLinks{
        justify-content: center;
        position: relative;
        top: 11px
    }
    footer .socialLinks .socialLinks-item,
    footer .socialLinks .socialLinks-item:last-child{
        margin: 0 7px 14px 7px;
    }

    .footer-copyright{
        padding-top: 0;
        text-align: center;
    }

    .footer-payment-icons,
    .powered-by{
        flex: 1 1 100%;
    }

    .footer-payment-icons{
        border-bottom: 1px solid #4a4a4a;
        padding-top: 30px;
        padding-bottom: 30px;
    }

    .footer-copyright .powered-by{margin-top: 15px;}

    // product grid
    .productGrid .product{
        flex: 0 1 #{$numProds--grid-tablet}%;
        max-width: #{$numProds--grid-tablet}%;
    }

    // category page
    .page--category > .main > .container{
        display: flex;
        flex-flow: column nowrap;
    }
    .page--category .page-content{order: 1;}
    .page--category .page-sidebar{order: 2; margin-bottom: 20px;}
    .page--category #product-listing-container{order: 3;}

    .actionBar-section .form-field,
    .pagination-list{margin-bottom: 15px;}

    .page-sidebar{padding: 0;}
    .sidebar-nav-mobile{
        display: flex;
        flex-flow: row nowrap;
        list-style: none;
        margin: 0;
        position: relative;
        justify-content: space-between;
    }
    .grid-bg .sidebar-nav-mobile{
        background: #f9f9f9;
    }
    .sidebar-nav-mobile > a{
        display: flex;
        align-items: center;
        flex: 0 1 calc(50% - 6px);
        margin: 0;
        padding: 9px 15px;
        height: 45px;
    }
    .sidebar-nav-mobile > a:not(.is-open),
    .sidebar-nav-mobile > a:not(.is-open):hover,
    .sidebar-nav-mobile > a:not(.is-open):focus{
        background: stencilColor("color-greyLightest");
        border-color: stencilColor("card-image-border");
        color: $card-titleColor;
    }
    .sidebar-nav-mobile > a.is-open{
        position: absolute;
        width: 100%;
        z-index: 5;
    }

    .sidebarBlock + .sidebarBlock{
        border: 0;
        margin: 0;
        padding: 0;
    }

    .sidebarBlock > .facetedSearch-toggle{display: none;}
    .accordion .sidebarBlock-heading{
        border-bottom: 1px solid stencilColor("container-border-global-color-base");
        padding: 14px;
    }
    #subcategoryList:not(.is-open){display: none;}

    #subcategoryList .accordion .navList{padding: 0 14px 14px;}

    .facetedSearch-toggle-indicator{
        text-align: left;
        width: 100%;
    }

    .facetedSearch-toggle-indicator > .toggleLink-text{
        font-size: 14px;
        width: 100%;
    }

    .toggleLink-text span{
        vertical-align: middle;
        font-weight: bold;
    }

    .sidebarBlock .accordion{margin: 0;}

    .sidebar-nav-mobile .toggleLink-text.toggleLink-text--on i{
        border-radius: 100px;
        border: 2px solid $button--secondary-text;
        padding: 1px;
        width: 25px;
        height: 25px;
    }

    .sidebar-nav-mobile .toggleLink-text i{
        max-width: none;
        max-height: none;
        width: 19px;
        height: 16px;
        margin-right: 5px;
    }

    .page-sidebar .toggleLink.button svg{
        fill: $button--secondary-text;
    }
    .page-sidebar .toggleLink.button:hover svg,
    .page-sidebar .toggleLink.button:active svg{
        fill: $button--secondary-text-hover;
    }
    .facetedSearch-toggle-indicator .toggleLink-text--on svg{
        transform: rotate(180deg);
    }

    #product-listing-container .sort-and-pagination .actionBar-section{
        float: none;
    }

    .sort-and-pagination .actionBar-section select{
        width: 100%;
        max-width: calc(100% - 80px);
    }

    .sort-and-pagination > *{
        flex: 1 1 auto;
    }

    li.pagination-item.pagination-item--next{margin-right: 10px;}
    li.pagination-item.pagination-item--previous{margin-left: 10px;}

    // cart page
    .continue-shopping{
        clear: both;
        text-align: center;
    }

}

@media only screen and (min-width: 531px) and (max-width: 800px) {
    .blog-posts-wrap .blog:not(.full-post){
        display: flex;
        flex-flow: row nowrap;
        justify-content: space-between;
        padding: 10px 0;
    }

    .blog-posts-wrap .blog:not(.full-post) > div{
        flex: 0 1 calc(50% - 10px);
        margin: 0;
    }

    .blog-posts-wrap .blog:not(.full-post) figure{margin: 0;}
}

@media only screen and (min-width: 481px) and (max-width: 800px){
    // CATEGORY PAGE    
    .page-content-subcategories .subcategory-grid li{
        flex: 0 1 #{$numProds--grid-tablet}%;
    }
}

@media only screen and (min-width: 501px) and (max-width: 800px) {
    // CATEGORY PAGE
