    }
*/
/*
    .card .card-body{
        height: calc(100% - #{$productGrid-imageHeight--tablet});
    }
*/
}

@media only screen and (max-width: 530px) {
    h2.page-heading{
      font-size: 22px;
      font-weight: bold;
    }
}

@media only screen and (max-width: 500px) {
    // product grid
/*
    .card .card-figure{
        height: $productGrid-imageHeight--mobile;
    }
*/
/*
    .card .card-image{
        max-height: 100%;
    }
*/
/*
    .card .card-body{
        height: calc(100% - #{$productGrid-imageHeight--mobile});
    }
*/
}

@media only screen and (max-width: 480px) {
    // product grid
    .productGrid .product{
        flex: 0 1 #{$numProds--grid-mobile}%;
        max-width: #{$numProds--grid-mobile}%;
    }

    // CATEGORY PAGE
    .page-content-subcategories .subcategory-grid li{
        flex: 0 1 #{$numProds--grid-mobile}%;
    }

    // home page
    .promo-box-row li{
        flex: 0 1 100%;
        margin-bottom: 15px;
    }

    // FOOTER
    .footer-payment-icons{
        text-align: center;
    }
}
